mapper.select:
SELECT
-- 级联表的字段 ,huanjingbaohu.huanjingbaohu_name as huanjingbaohuName ,huanjingbaohu.address_types as addressTypes ,huanjingbaohu.huanjingbaohu_photo as huanjingbaohuPhoto ,huanjingbaohu.huanjingbaohu_size as huanjingbaohuSize ,huanjingbaohu.zan_number as zanNumber ,huanjingbaohu.cai_number as caiNumber ,huanjingbaohu.huanjingbaohu_content as huanjingbaohuContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM huanjingbaohu_liuyan a left JOIN huanjingbaohu huanjingbaohu ON a.huanjingbaohu_id = huanjingbaohu.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.huanjingbaohu_id = #{params.huanjingbaohuId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.huanjingbaohu_liuyan_text like CONCAT('%',#{params.huanjingbaohuLiuyanText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.huanjingbaohu_id IS NOT NULL
and huanjingbaohu.huanjingbaohu_name like CONCAT('%',#{params.huanjingbaohuName},'%')
and huanjingbaohu.address_types = #{params.addressTypes}
and huanjingbaohu.huanjingbaohu_size like CONCAT('%',#{params.huanjingbaohuSize},'%')
and huanjingbaohu.zan_number >= #{params.zanNumberStart}
and huanjingbaohu.zan_number <= #{params.zanNumberEnd}
and huanjingbaohu.zan_number = #{params.zanNumber}
and huanjingbaohu.cai_number >= #{params.caiNumberStart}
and huanjingbaohu.cai_number <= #{params.caiNumberEnd}
and huanjingbaohu.cai_number = #{params.caiNumber}
and huanjingbaohu.huanjingbaohu_content like CONCAT('%',#{params.huanjingbaohuContent},'%')
and UNIX_TIMESTAMP(huanjingbaohu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(huanjingbaohu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
